pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol";

contract FanszoidMarketplace is ReentrancyGuard {
    using Counters for Counters.Counter;

    address payable public _owner;
    address public fansNFTContract;
    Counters.Counter private _eventIds;

    uint256 public constant MINIMUM_ASK = 0.005 ether;

    struct CreatorRoyalty {
        address payable creator;
        uint256 royalty;
    }

    struct Event {
        address organizer;
        uint256[] ticketIds;
    }

    mapping(uint256 => Event) public events;

    mapping(uint256 => CreatorRoyalty) public creatorRoyalties;

    mapping(address => mapping(uint256 => uint256)) public askingPrices;
    
    mapping(address => mapping(uint256 => uint256)) public globalBids;

    constructor() {
        _owner = payable(msg.sender);
    }

    modifier onlyNFTContract() {
        require(fansNFTContract == msg.sender, "FanszoidMarketplace: Only the nft contract allowed");
        _;
    }

    function configure(address _fansNFTContract) external {
        require(fansNFTContract == address(0), "FanszoidMarketplace: The FanszoidMarketplace was already configured");
        require(msg.sender == _owner, "FanszoidMarketplace: Only owner can configure the FanszoidMarketplace");
        require(_fansNFTContract != address(0), "FanszoidMarketplace: nftContract cannot be 0" );


        fansNFTContract = _fansNFTContract;
    }

    function calculateFee( uint totalAmount, uint fee ) private pure returns(uint) {
        require((totalAmount / 10000) * 10000 == totalAmount, 'FanszoidMarketplace: amount is too small on to calculate fee');
        require(fee <= 10000 , 'FanszoidMarketplace: fee is too large on calculateFee');
        return totalAmount * fee / 10000;
    }

    event eventCreated(
        uint256 eventId,
        address organizer
    );

    function createEvent() public nonReentrant returns(uint256) {

        _eventIds.increment();
        events[_eventIds.current()] = Event(msg.sender, new uint256[](0));

        emit eventCreated(_eventIds.current(), msg.sender);

        return _eventIds.current();
    }

    function publishEventTickets( uint256 eventId, uint256 ticketTypesCount, uint256[] calldata ticketIds, uint256[] calldata publishingAskingPrices, uint256[] calldata publishingCreatorRoyalties, address payable publisher ) public nonReentrant onlyNFTContract {
        require(payable(events[eventId].organizer) != address(0), "FanszoidMarketplace: Non-existant event.");
        require(payable(events[eventId].organizer) == publisher, "FanszoidMarketplace: Only the organizer can publish tickets for the event.");
        
        for (uint i = 0; i < ticketTypesCount; i++) {
            creatorRoyalties[ticketIds[i]] = CreatorRoyalty(publisher, publishingCreatorRoyalties[i]);
            askingPrices[publisher][ticketIds[i]] = publishingAskingPrices[i];
            events[eventId].ticketIds.push(ticketIds[i]);
        }
    }

    event ticketBought(
        uint256 tokenId,
        address seller,
        address buyer,
        uint256 price
    );

    function buyMarketTicket( uint256 tokenId, address seller ) public payable nonReentrant {
        require(IERC1155(fansNFTContract).balanceOf(seller, tokenId) > 0, "FanszoidMarketplace: Seller has no ticket.");
        require(askingPrices[seller][tokenId] >= MINIMUM_ASK && msg.value == askingPrices[seller][tokenId], "FanszoidMarketplace: ticket is not for sale or Asking price was not matched in message value.");

        uint askingPrice = askingPrices[seller][tokenId];
        address payable creator = creatorRoyalties[tokenId].creator;
        
        uint fanszoidShare = calculateFee(msg.value, 250);
        uint creatorShare = calculateFee(msg.value, creatorRoyalties[tokenId].royalty);
        uint sellerShare = msg.value - fanszoidShare - creatorShare;

        payable(seller).transfer(sellerShare);
        creator.transfer(creatorShare);
        _owner.transfer(fanszoidShare);

        IERC1155(fansNFTContract).safeTransferFrom( address(seller) , msg.sender, tokenId, 1, "");

        emit ticketBought(tokenId, seller, msg.sender, askingPrice);
    }

    event AskSetted(
        uint256 tokenId,
        address seller,
        uint256 askingPrice
    );

    function setAsk(uint256 tokenId, uint askingPrice, address seller) public onlyNFTContract
    {
        require(askingPrice >= 0.05 ether, "FanszoidMarketplace: Minimum ask is 0.05 ether.");
        require(IERC1155(fansNFTContract).balanceOf(seller, tokenId) > 0, "FanszoidMarketplace: Sender has no ticket.");

        // TO DO: verify asking price is divisible by the token Shares

        askingPrices[seller][tokenId] = askingPrice;
        emit AskSetted(tokenId, seller, askingPrice);
    }

    event AskRemoved(
        address seller,
        uint256 tokenId
    );

    function removeAsk(uint256 tokenId) external
    {
        require(IERC1155(fansNFTContract).balanceOf(msg.sender, tokenId) > 0, "FanszoidMarketplace: Sender has no ticket.");

        delete askingPrices[msg.sender][tokenId];
        emit AskRemoved(msg.sender, tokenId);
    }

}","keccak256":"0x1975f9221d0fb4ec96c4d5b69b61eba548d5bb64b08bd9785035e9a45a7fc610","license":"UNLICENSED"},"src/TicketsNFT.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "./FanszoidMarketplace.sol";
import "hardhat/console.sol";


contract TicketsNFT is ERC1155, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;
    address public marketplaceAddress;

    uint256 public constant MINIMUM_ASK = 0.005 ether;

    constructor(address _marketplaceAddress) ERC1155("https://fanszoid.com/api/{id}.json") {
        marketplaceAddress = _marketplaceAddress;
    }

    event ticketsPublished(
        uint256 eventId,
        uint256 ticketTypesCount, 
        uint256[] newTokenIds, 
        uint256[] amounts, 
        uint256[] askingPrices, 
        uint256[] creatorRoyalties,
        address publisher
    );

    function publishEventTickets(uint256 eventId, uint256 ticketTypesCount, uint256[] calldata amounts, uint256[] calldata askingPrices, uint256[] calldata creatorRoyalties) public nonReentrant {
        require(ticketTypesCount <= 100 , "TicketsNFT: Too much tickets!");
        for (uint i = 0; i < ticketTypesCount; i++) {
            require(creatorRoyalties[i] <= 500 , "TicketsNFT: max creatorRoyalty supported is 5%");
            require(askingPrices[i] >= MINIMUM_ASK , "TicketsNFT: minimum asking price not matched." );
        }

        uint256[] memory newTokenIds = new uint[](ticketTypesCount);

        for (uint i = 0; i < ticketTypesCount; i++) {
            _tokenIds.increment();
            newTokenIds[i] = _tokenIds.current();
        }
        
        _mintBatch(msg.sender, newTokenIds, amounts, "");
        setApprovalForAll(marketplaceAddress, true);
        
        FanszoidMarketplace(marketplaceAddress).publishEventTickets(eventId, ticketTypesCount, newTokenIds, askingPrices, creatorRoyalties, payable(msg.sender));

        emit ticketsPublished(
            eventId,
            ticketTypesCount, 
            newTokenIds, 
            amounts, 
            askingPrices, 
            creatorRoyalties,
            msg.sender
        );
    }

    function setAsk(uint256 tokenId, uint askingPrice) external nonReentrant
    {
        FanszoidMarketplace(marketplaceAddress).setAsk(tokenId, askingPrice, msg.sender);
        setApprovalForAll(marketplaceAddress, true);
    }

    function removeAsk(uint256 tokenId) external nonReentrant
    {
        FanszoidMarketplace(marketplaceAddress).removeAsk(tokenId);
    }

}","keccak256":"0x58b5744936848297d1ea920a3fe1c281aa0a59af60774f48aad3d145ca46913e","license":"UNLICENSED"}},"version":1}
